import { React, useState, useEffect } from 'react'
import { useParams } from 'react-router-dom'
import BranchView from '../Components/branches/BranchView'
import { axiosTokenHeader, tokenGet } from '../utils/functions'
import Helmet from 'react-helmet'
import axios from 'axios'

function Branch(props) {
    const {p} = props
    const [data, setData] = useState(null);
    const [origin, setOrigin] = useState({})
    const [isPending, setIsPending] = useState(true);
    const [error, setError] = useState(null);
    const { isDark, apiBase, fontSize, apiBase2, token } = props.p
    const { id } = useParams()
    let url;
    if (id) {
        url = apiBase2 + '/branches/nested?id=' + id
    } else {
        url = apiBase2 + '/branches/root'
    }

    const getBranchData = (data) => {
        const childID = data[0].id
        axios.get(apiBase2 + '/branches/origin?id' + childID, axiosTokenHeader(p.token)).then(e => {
            console.log(e)
        }).catch(r => {
            console.log(r)
        })
    }

    const freshBranches = () => {
        tokenGet(url, { Authorization: token })
            .then(data => {
                setIsPending(false);
                setData(data);
                getBranchData(data)
                setError(null);
            })
            .catch(err => {
                if (err.name === 'AbortError') {
                    console.log('fetch aborted')
                } else {
                    // auto catches network / connection error
                    setIsPending(false);
                    setError(err.message);
                }
            })
    }

    useEffect(() => {

        freshBranches()

    }, [url])


    useEffect(() => {
        if (data) {
            const url = apiBase2 + '/branches/origin?id=' + data[0].parentID
            console.log('url', url)
        }
    }, [])

    const ex = {
        freshBranches
    }

    const getTitle = () => {
        if (data && data.name) return data.name
        else return 'Me 4 Life'
    }

    return (
        <div>
            {data &&
                <div className={'wrapper'}>
                    <Helmet>
                        <title>{getTitle()}</title>
                    </Helmet>
                    <BranchView isDark={isDark} data={data} isPending={isPending} apiBase={apiBase} setUpToDate={setData} fontSize={fontSize} p={props.p} ex={ex} />
                </div>

            }
            {isPending && <i className="fas fa-spinner spinner"></i>}
            {error && <div> {error} </div>}
        </div>
    )
}

export default Branch
